//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.11;

import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/IERC1155ReceiverUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/introspection/ERC165Upgradeable.sol";


import "./ArtToken.sol";


/// @title An Auction Contract for bidding and selling single and batched NFTs
/// @notice This contract can be used for auctioning any NFTs, and accepts any ERC20 token as payment
contract Marketplace is 
    OwnableUpgradeable, 
    ReentrancyGuardUpgradeable, 
    ERC165Upgradeable, 
    IERC1155ReceiverUpgradeable 
{
    using SafeMathUpgradeable for uint256;
    using CountersUpgradeable for CountersUpgradeable.Counter;

    CountersUpgradeable.Counter internal _auctionIdCounter;

    /// @notice auctionId --> Auction
    mapping(uint256 => Auction) public idToAuction;
    /// @notice autionId --> bidId --> Bid
    mapping(uint256 => mapping(uint256 => Bid)) public auctionToBid;
    /// @notice nftContractAddress --> isApproved
    mapping(address => bool) public isTrustedNftContract;
    /// @notice Failed transfer ETHs
    mapping(address => uint256) public ethCredits;
    /// @notice Failed transfer tokens
    mapping(address => uint256) public tokenCredits;

    struct Auction {
        /// @notice 0 = fixed-price sale, 1 = timed-auction, 2 = declining sale
        uint8 auctionType;
        /// @notice NFT contract holding NFTs for sale
        address nftContractAddress;
        uint256[] tokenIds;
        uint256[] quantities;
        /// @notice Amounts of NFTs transferred to marketplace at the last transfer
        uint256[] transferredQuantities;
        uint32 bidIncreasePercentage;
        /// @notice Reserve price for auction, or minimum price in declining sale
        uint128 minPrice;
        /// @notice This is used in fixed-price sale as well
        uint128 buyNowPrice;
        address nftSeller;
        /// @notice The seller can specify a whitelisted address for a sale (this is effectively a direct sale)
        address whitelistedBuyer;
        /// @notice This bid ID must be always valid
        uint256 highestBidId;
        uint256 numBids;
        /// @notice Whether auction is on-going
        bool isOnGoing;
        /// @notice Auction start time (used in declining-price sale)
        uint256 startTime;
        /// @notice The time, for which NFT price decreases from buyNowPrice to minPrice (used in declining-price sale)
        uint256 decrementDuration;
    }

    struct Bid {
        address bidder;
        uint256 bid;
        /// @notice The bidder can specify a recipient for the NFT if their bid is successful
        address nftRecipient;
        bool isOffer;
    }

    /// @notice Default values that are used if not specified by the NFT seller
    uint32 public defaultBidIncreasePercentage;
    uint32 public minimumSettableIncreasePercentage;

    /// @notice Fees need to be paid for successful purchase
    address public marketplaceWallet;
    uint256 public marketplaceFeePortion;

    /// @notice ERC20 token used for timed-auction payment
    address public erc20TokenForAuction;

    /*╔═════════════════════════════╗
      ║           EVENTS            ║
      ╚═════════════════════════════╝*/
    event MarketplaceWalletChanged(address marketplaceWallet);

    event MarketplaceFeePortionChanged(uint256 marketplaceFeePortion);

    event ERC20TokenForAuctionChanged(address erc20TokenForAuction);

    event NftTokenContractTrusted(address nftContractAddress, bool isTrusted);

    event NftAuctionCreated(uint256 auctionId);

    event SaleCreated(uint256 auctionId);

    event DecliningPriceSaleCreated(uint256 auctionId);

    event BidMade(uint256 auctionId, uint256 bidId, address bidder, uint256 ethAmount);

    event NFTTransferredAndSellerPaid(
        uint256 auctionId, 
        uint256 bidId,
        uint256[] tokenIds,
        uint256[] quantities,
        uint256 price,
        address to,
        address from,
        uint256 blockTimestamp
    );

    event AuctionStopped(uint256 auctionId);

    event AuctionSettled(uint256 auctionId, uint256 _bidId);

    event AuctionWithdrawn(uint256 auctionId);

    event BidWithdrawn(uint256 auctionId, uint256 bidId);

    event WhitelistedBuyerUpdated(uint256 auctionId, address newWhitelistedBuyer);

    event MinimumPriceUpdated(uint256 auctionId, uint256 newMinPrice);

    event BuyNowPriceUpdated(uint256 auctionId, uint128 newBuyNowPrice);

    event OfferTaken(uint256 auctionId, uint256 bidId);

    event CreditWithdrawn(uint256 ethAmount, uint256 tokenAmount);

    /**********************************/
    /*╔═════════════════════════════╗
      ║             END             ║
      ║            EVENTS           ║
      ╚═════════════════════════════╝*/
    /**********************************/
    /*╔═════════════════════════════╗
      ║          MODIFIERS          ║
      ╚═════════════════════════════╝*/
    /// @notice Check if nftContractAddress is trusted or not
    modifier isNftContractTrusted(address _nftContractAddress) {
        require(
            isTrustedNftContract[_nftContractAddress],
            "Marketplace.isNftContractTrusted# UNAUTHORIZED_NFT_CONTRACT"
        );
        _;
    }

    /// @notice Check if msg.sender has enough NFTs for sale
    modifier enoughTokensForSale(
        address _nftContractAddress,
        uint256[] memory _tokenIds,
        uint256[] memory _quantities
    ) {
        ArtToken arttoken = ArtToken(_nftContractAddress);
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            bool isCreator = msg.sender == arttoken.creators(_tokenIds[i]);
            bool isOwner = arttoken.balanceOf(msg.sender, _tokenIds[i]) >= _quantities[i];
            require(
                isCreator || isOwner, 
                "Marketplace.enoughTokensForSale# NOT_ENOUGH_TOKENS_FOR_SALE"
            );
        }
        _;
    }

    modifier auctionOngoing(uint256 _auctionId) {
        require(
            idToAuction[_auctionId].isOnGoing,
            "Marketplace.auctionOngoing# ENDED_AUCTION"
        );
        _;
    }

    /// @notice The minimum price must be less than the buyNowPrice (if set)
    modifier minPriceDoesNotExceedLimit(uint256 _buyNowPrice, uint256 _minPrice) {
        require(
            (_buyNowPrice == 0) || (_buyNowPrice >= _minPrice),
            "Marketplace.minPriceDoesNotExceedLimit# MIN_PRICE_HIGHER_THAN_BUYNOW_PRICE"
        );
        _;
    }

    modifier onlyNftSeller(uint256 _auctionId) {
        require(
            msg.sender == idToAuction[_auctionId].nftSeller,
            "Marketplace.onlyNftSeller# ONLY_NFT_SELLER"
        );
        _;
    }

    // /**********************************/
    // /*╔═════════════════════════════╗
    //   ║             END             ║
    //   ║          MODIFIERS          ║
    //   ╚═════════════════════════════╝*/
    // /**********************************/
    function initialize(
        address _marketplaceWallet, 
        uint256 _marketplaceFeePortion,
        address _erc20TokenForAuction
    ) public initializer {
        __Ownable_init();

        defaultBidIncreasePercentage = 100;
        minimumSettableIncreasePercentage = 100;

        marketplaceWallet = _marketplaceWallet;
        marketplaceFeePortion = _marketplaceFeePortion;
        erc20TokenForAuction = _erc20TokenForAuction;
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external override returns(bytes4) {
        return bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"));
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external override returns(bytes4) {
        return bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"));
    }

    /*╔══════════════════════════════╗
      ║    AUCTION CHECK FUNCTIONS   ║
      ╚══════════════════════════════╝*/
    /// @notice Check if a bid has been made. This is applicable in the early bid scenario
    /// to ensure that if an auction is created after an early bid, the auction
    /// begins appropriately or is settled if the buy now price is met.
    function _isABidMade(uint256 _auctionId) internal view returns (bool) {
        return (idToAuction[_auctionId].numBids > 0);
    }

    function _isBidMade(uint256 _auctionId, uint256 _bidId) 
    internal view returns (bool) {
        return auctionToBid[_auctionId][_bidId].bid > 0;
    }

    /// @notice If the buy now price is set by the seller, check that the highest bid meets that price.
    function _isBuyNowPriceMet(uint256 _auctionId, uint256 _bidId) 
    internal view returns (bool) {
        uint128 buyNowPrice = idToAuction[_auctionId].buyNowPrice;
        return buyNowPrice > 0 && (auctionToBid[_auctionId][_bidId].bid >= buyNowPrice);
    }

    /// @notice Check that a bid is applicable for the purchase of the NFT.
    /// In the case of a sale: the bid needs to meet the buyNowPrice.
    /// In the case of an auction: the bid needs to be a % higher than the previous bid.
    function _doesBidMeetBidRequirements(
        uint256 _auctionId, 
        uint128 _tokenAmount, 
        bool _isOffer
    ) internal view returns (bool) {
        uint128 buyNowPrice = idToAuction[_auctionId].buyNowPrice;
        //if buyNowPrice is met, ignore increase percentage
        if (buyNowPrice > 0 && (msg.value >= buyNowPrice || _tokenAmount >= buyNowPrice)) {
            return true;
        }
        if (idToAuction[_auctionId].auctionType == 1) {
            //if the NFT is up for auction, the bid needs to be a % higher than the previous bid
            if (idToAuction[_auctionId].numBids > 0) {
                Bid memory highestBid = auctionToBid[_auctionId][idToAuction[_auctionId].highestBidId];
                uint256 bidIncreaseAmount = (
                    highestBid.bid * (10000 + _getBidIncreasePercentage(_auctionId))
                ) / 10000;
                return (msg.value >= bidIncreaseAmount) || (_tokenAmount >= bidIncreaseAmount);
            } else {
                uint256 minPrice = idToAuction[_auctionId].minPrice;
                return (msg.value >= minPrice) || (_tokenAmount >= minPrice);
            }
        } else if (idToAuction[_auctionId].auctionType == 2) {
            // if the NFT is up for declining-price sale, the bid needs to be at least the current price
            uint256 currentPrice = _computeCurrentPrice(_auctionId);
            return (msg.value >= currentPrice) || (_tokenAmount >= currentPrice);
        } else {
            // if the NFT is up for fixed-price sale or declining sale, and the bid is below the buy-now price, only offers are accepted
            return _isOffer;
        }
    }

    function _isWhitelistedSale(uint256 _auctionId) 
    internal view returns (bool) {
        return (idToAuction[_auctionId].whitelistedBuyer != address(0));
    }

    /// @notice Check if payment type is matched
    function _isPaymentAccepted(uint256 _auctionId, uint128 _tokenAmount) 
    internal view returns (bool) {
        if (_isERC20Auction(_auctionId)) {
            return (msg.value == 0) && (_tokenAmount > 0);
        } else {
            return (msg.value != 0) && (_tokenAmount == 0);
        }
    }

    function _isERC20Auction(uint256 _auctionId) internal view returns (bool) {
        return (idToAuction[_auctionId].auctionType != 0);
    }

    /// @notice Returns the percentage of the total bid (used to calculate fee payments)
    function _getPortionOfBid(uint256 _totalBid, uint256 _percentage)
    internal pure returns (uint256) {
        return (_totalBid * _percentage).div(10000);
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║    AUCTION CHECK FUNCTIONS   ║
      ╚══════════════════════════════╝*/
    /**********************************/
    /*╔══════════════════════════════╗
      ║    DEFAULT GETTER FUNCTIONS  ║
      ╚══════════════════════════════╝*/
    /*****************************************************************
     * These functions check if the applicable auction parameter has *
     * been set by the NFT seller. If not, return the default value. *
     *****************************************************************/
    function _getBidIncreasePercentage(uint256 _auctionId) 
    internal view returns (uint32) {
        uint32 bidIncreasePercentage = idToAuction[_auctionId].bidIncreasePercentage;

        if (bidIncreasePercentage == 0) {
            return defaultBidIncreasePercentage;
        } else {
            return bidIncreasePercentage;
        }
    }

    /// @notice The default value for the NFT recipient is the highest bidder
    function _getNftRecipient(uint256 _auctionId, uint256 _bidId) 
    internal view returns (address) {
        address nftRecipient = auctionToBid[_auctionId][_bidId].nftRecipient;

        if (nftRecipient == address(0)) {
            return auctionToBid[_auctionId][_bidId].bidder;
        } else {
            return nftRecipient;
        }
    }

