    function setMarketplaceWallet(address _marketplaceWallet) 
    external onlyOwner {
        require(
            _marketplaceWallet != address(0), 
            "Marketplace.setMarketplace# INVALID_WALLET_ADDRESS"
        );
        marketplaceWallet = _marketplaceWallet;
        emit MarketplaceWalletChanged(marketplaceWallet);
    }

    function setMarketplaceFeePortion(uint256 _marketplaceFeePortion) 
    external onlyOwner {
        marketplaceFeePortion = _marketplaceFeePortion;
        emit MarketplaceFeePortionChanged(marketplaceFeePortion);
    }

    function setERC20TokenForAuction(address _erc20TokenForAuction) 
    external onlyOwner {
        require(
            _erc20TokenForAuction != address(0), 
            "Marketplace.setERC20TokenForAuction# INVALID_ERC20_ADDRESS"
        );
        erc20TokenForAuction = _erc20TokenForAuction;
        emit ERC20TokenForAuctionChanged(erc20TokenForAuction);
    }

    function setTrustedNftContract(address _nftContractAddress, bool _isTrusted) 
    external onlyOwner {
        isTrustedNftContract[_nftContractAddress] = _isTrusted;
        emit NftTokenContractTrusted(_nftContractAddress, _isTrusted);
    }

