    /*╔══════════════════════════════╗
      ║  TRANSFER NFTS TO CONTRACT   ║
      ╚══════════════════════════════╝*/
    /// @notice Transfer NFTs, without minting, to auction contract, to create an auction
    function _transferNftToAuctionContract(uint256 _auctionId) 
    internal nonReentrant {
        // whether seller have ability to supply the NFTs has been checked at ``enoughTokensForSale``
        address nftSeller = idToAuction[_auctionId].nftSeller;
        uint256[] memory tokenIds = idToAuction[_auctionId].tokenIds;
        uint256[] memory quantities = idToAuction[_auctionId].quantities;

        ArtToken arttoken = ArtToken(idToAuction[_auctionId].nftContractAddress);

        for (uint256 i = 0; i < tokenIds.length; i++) {
            uint256 currentBalance = arttoken.balanceOf(nftSeller, tokenIds[i]);
            if (currentBalance < quantities[i]) {
                idToAuction[_auctionId].transferredQuantities.push(currentBalance);
            } else {
                idToAuction[_auctionId].transferredQuantities.push(quantities[i]);
            }
        }

        arttoken.safeBatchTransferFrom(
            nftSeller, 
            address(this), 
            tokenIds, 
            idToAuction[_auctionId].transferredQuantities, 
            ""
        );
    }

    /// @notice Withdraw NFTs from auction contract, to terminate an auction without selling NFTs
    function _withdrawNftFromAuctionContract(uint256 _auctionId) 
    internal nonReentrant {
        ArtToken arttoken = ArtToken(idToAuction[_auctionId].nftContractAddress);
        address nftSeller = idToAuction[_auctionId].nftSeller;
        arttoken.safeBatchTransferFrom( 
            address(this), 
            nftSeller,
            idToAuction[_auctionId].tokenIds, 
            idToAuction[_auctionId].transferredQuantities, 
            ""
        );
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║  TRANSFER NFTS TO CONTRACT   ║
      ╚══════════════════════════════╝*/
    /**********************************/

    /*╔══════════════════════════════╗
      ║       AUCTION CREATION       ║
      ╚══════════════════════════════╝*/

    /// @notice Create an auction using custom bid increase percentage & custom auction bid period
    function createNftAuction(
        address _nftContractAddress,
        uint256[] memory _tokenIds,
        uint256[] memory _quantities,
        uint128 _minPrice,
        uint128 _buyNowPrice,
        uint32 _bidIncreasePercentage
    )
        external
        isNftContractTrusted(_nftContractAddress)
        enoughTokensForSale(_nftContractAddress, _tokenIds, _quantities)
        minPriceDoesNotExceedLimit(_buyNowPrice, _minPrice)
    {
        require(
            _nftContractAddress != address(0), 
            "Marketplace.createSale# INVALID_NFT_TOKEN_CONTRACT"
        );
        require(
            _bidIncreasePercentage >= minimumSettableIncreasePercentage,
            "Marketplace.createNftAuction# TOO_LOW_BID_INCREASE_PERCENTAGE"
        );

        // set up information about the auction
        uint256 auctionId = _auctionIdCounter.current();
        idToAuction[auctionId].auctionType = 1;
        idToAuction[auctionId].nftContractAddress = _nftContractAddress;
        idToAuction[auctionId].tokenIds = _tokenIds;
        idToAuction[auctionId].quantities = _quantities;
        idToAuction[auctionId].bidIncreasePercentage = _bidIncreasePercentage;
        idToAuction[auctionId].minPrice = _minPrice;
        idToAuction[auctionId].buyNowPrice = _buyNowPrice;
        idToAuction[auctionId].nftSeller = msg.sender;
        idToAuction[auctionId].isOnGoing = true;
        _auctionIdCounter.increment();

        // transfer required NFTs to marketplace
        // otherwise seller will mint them later
        _transferNftToAuctionContract(auctionId);

        emit NftAuctionCreated(auctionId);
    }

    /// @notice Create fixed-price sale
    function createSale(
        address _nftContractAddress,
        uint256[] memory _tokenIds,
        uint256[] memory _quantities,
        uint128 _buyNowPrice,
        address _whitelistedBuyer
    ) 
        external
        isNftContractTrusted(_nftContractAddress)
        enoughTokensForSale(_nftContractAddress, _tokenIds, _quantities) 
    {
        require(
            _nftContractAddress != address(0), 
            "Marketplace.createSale# INVALID_NFT_TOKEN_CONTRACT"
        );

        // set up information about the sale
        uint256 auctionId = _auctionIdCounter.current();
        idToAuction[auctionId].auctionType = 0;
        idToAuction[auctionId].nftContractAddress = _nftContractAddress;
        idToAuction[auctionId].tokenIds = _tokenIds;
        idToAuction[auctionId].quantities = _quantities;
        idToAuction[auctionId].buyNowPrice = _buyNowPrice;
        idToAuction[auctionId].nftSeller = msg.sender;
        idToAuction[auctionId].whitelistedBuyer = _whitelistedBuyer;
        idToAuction[auctionId].isOnGoing = true;
        _auctionIdCounter.increment();

        // transfer required NFTs to marketplace
        // otherwise seller will mint them later
        _transferNftToAuctionContract(auctionId);

        emit SaleCreated(auctionId);
    }

    /// @notice Create fixed-price sale
    function createDecliningPriceSale(
        address _nftContractAddress,
        uint256[] memory _tokenIds,
        uint256[] memory _quantities,
        uint128 _startPrice,
        uint128 _endPrice,
        uint128 _decrementDuration
    ) 
        external
        isNftContractTrusted(_nftContractAddress)
        enoughTokensForSale(_nftContractAddress, _tokenIds, _quantities) 
    {
        require(
            _nftContractAddress != address(0), 
            "Marketplace.createSale# INVALID_NFT_TOKEN_CONTRACT"
        );
        require(
            _startPrice >= _endPrice, 
            "Marketplace.createDecliningPriceSale# START_PRICE_LOWER_THAN_END_PRICE"
        );
        require(
            _decrementDuration > 0,
            "Marketplace.createDecliningPriceSale# INVALID_DECREMENT_DURATION"
        );

        // set up information about the sale
        uint256 auctionId = _auctionIdCounter.current();
        idToAuction[auctionId].auctionType = 2;
        idToAuction[auctionId].nftContractAddress = _nftContractAddress;
        idToAuction[auctionId].tokenIds = _tokenIds;
        idToAuction[auctionId].quantities = _quantities;
        idToAuction[auctionId].minPrice = _endPrice;
        idToAuction[auctionId].buyNowPrice = _startPrice;
        idToAuction[auctionId].startTime = block.timestamp;
        idToAuction[auctionId].decrementDuration = _decrementDuration;
        idToAuction[auctionId].nftSeller = msg.sender;
        idToAuction[auctionId].isOnGoing = true;
        _auctionIdCounter.increment();

        // transfer required NFTs to marketplace
        // otherwise seller will mint them later
        _transferNftToAuctionContract(auctionId);

        emit DecliningPriceSaleCreated(auctionId);
    }

