    /*╔═════════════════════════════╗
      ║        BID FUNCTIONS        ║
      ╚═════════════════════════════╝*/

    /********************************************************************
     * Make bids with ETH or an ERC20 Token specified by the NFT seller.*
     * Additionally, a buyer can pay the asking price to conclude a sale*
     * of an NFT.                                                      *
     ********************************************************************/
    /// @notice Make offer
    /// @param _nftRecipient    Address of the NFT recipient if successful purchase. This should be zero if no recipient is indicated
    function makeBid(
        uint256 _auctionId, 
        address _nftRecipient, 
        uint128 _tokenAmount, 
        bool _isOffer
    ) external payable auctionOngoing(_auctionId) {
        require(
            msg.sender != idToAuction[_auctionId].nftSeller,
            "Marketplace.makeBid# SELLER_CANNOT_BID_HIS_SALE"
        );
        require(
            _isPaymentAccepted(_auctionId, _tokenAmount), 
            "Marketplace.makeBid# NOT_ACCEPTED_PAYMENT"
        );
        require(
            _doesBidMeetBidRequirements(_auctionId, _tokenAmount, _isOffer),
            "Marketplace.makeBid# NOT_ENOUGH_FUND_TO_BID"
        );
        // Check if the highest bidder can purchase this NFT
        require(
            !_isWhitelistedSale(_auctionId) || 
            (idToAuction[_auctionId].whitelistedBuyer == msg.sender),
            "Marketplace.makeBid# NOT_WHITELISTED_BUYER"
        );

        // create bid
        uint256 numBids = idToAuction[_auctionId].numBids;
        auctionToBid[_auctionId][numBids].bidder = msg.sender;
        auctionToBid[_auctionId][numBids].nftRecipient = _nftRecipient;
        auctionToBid[_auctionId][numBids].isOffer = _isOffer;
        idToAuction[_auctionId].numBids++;

        // transfer cash to marketplace and update highest bid
        uint256 highestBidId = idToAuction[_auctionId].highestBidId;
        if (_isERC20Auction(_auctionId)) {
            // transfer cash to marketplace
            IERC20Upgradeable(erc20TokenForAuction).transferFrom(msg.sender, address(this), _tokenAmount);
            auctionToBid[_auctionId][numBids].bid = _tokenAmount;

            // update highest bid
            if (_tokenAmount > auctionToBid[_auctionId][highestBidId].bid)
                idToAuction[_auctionId].highestBidId = numBids;

            emit BidMade(_auctionId, numBids, msg.sender, _tokenAmount);
        } else {
            // transfer cash to marketplace
            auctionToBid[_auctionId][numBids].bid = msg.value;

            // update highest bid
            if (msg.value > auctionToBid[_auctionId][highestBidId].bid)
                idToAuction[_auctionId].highestBidId = numBids;

            emit BidMade(_auctionId, numBids, msg.sender, msg.value);
        }

        if (_isBuyNowPriceMet(_auctionId, numBids)) {
            // finish auction if buy-now price is met, or current price is met
            _transferNftAndPaySeller(_auctionId, numBids);
            return;
        } else if (idToAuction[_auctionId].auctionType == 2) {
            // if ``auctionType == 2`` then ``_doesBidMeetBidRequirements`` implies that the bid meets the current price, hence transfer the NFTs immediately
            _transferNftAndPaySeller(_auctionId, numBids);
            return;
        }
    }

    /// @notice Compute current price for declining-price sale
    function _computeCurrentPrice(uint256 _auctionId) 
    internal view returns (uint256) {
        uint256 endPricePortion = (
            block.timestamp - idToAuction[_auctionId].startTime
        ) * 10000 / idToAuction[_auctionId].decrementDuration;
        endPricePortion = (endPricePortion > 10000)? 10000 : endPricePortion;
        uint256 startPricePortion = 10000 - endPricePortion;
        uint256 currentPrice = (
            startPricePortion * idToAuction[_auctionId].buyNowPrice +
            endPricePortion * idToAuction[_auctionId].minPrice
        ) / 10000;
        return currentPrice;
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║        BID FUNCTIONS         ║
      ╚══════════════════════════════╝*/
    /**********************************/

    /*╔══════════════════════════════╗
      ║        CLEAN AUCTION         ║
      ╚══════════════════════════════╝*/
    // @notice Withdraw and delete all bids, then delete auction 
    function _cleanAuction(uint256 _auctionId) internal {
        _withdrawAllBids(_auctionId);
        delete idToAuction[_auctionId];
    }

    /// @notice Withdraw all bids and save bidded amounts as credits
    function _withdrawAllBids(uint256 _auctionId) internal {
        uint256 numBids = idToAuction[_auctionId].numBids;
        if (_isERC20Auction(_auctionId)) {
            for (uint256 i = 0; i < numBids; i++) {
                if (_isBidMade(_auctionId, i)) {
                    Bid memory bid = auctionToBid[_auctionId][i];
                    tokenCredits[bid.bidder] += bid.bid;
                    delete auctionToBid[_auctionId][i];
                }
            }
        } else {
            for (uint256 i = 0; i < numBids; i++) {
                if (_isBidMade(_auctionId, i)) {
                    Bid memory bid = auctionToBid[_auctionId][i];
                    ethCredits[bid.bidder] += bid.bid;
                    delete auctionToBid[_auctionId][i];
                }
            }
        }
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║        CLEAN AUCTION         ║
      ╚══════════════════════════════╝*/
    /**********************************/
    /**********************************/
    /*╔══════════════════════════════╗
      ║  TRANSFER NFT & PAY SELLER   ║
      ╚══════════════════════════════╝*/
    /// @notice Transfer NFT and pay seller
    /// @dev Pay marketplace fee and seller fee, lazily mint unavailable NFTs, transfer them to seller, delete the chosen bid, withdraw all other bids, then delete the auction
    function _transferNftAndPaySeller(uint256 _auctionId, uint256 _bidId) internal {
        address _nftSeller = idToAuction[_auctionId].nftSeller;
        uint256 _nftBid = auctionToBid[_auctionId][_bidId].bid;

        // pay marketplace fee and seller fee
        uint256 marketplaceFee = _getPortionOfBid(_nftBid, marketplaceFeePortion);
        uint256 sellerFee = _nftBid - marketplaceFee;
        if (_isERC20Auction(_auctionId)) {
            IERC20Upgradeable erc20 = IERC20Upgradeable(erc20TokenForAuction);
            erc20.transfer(marketplaceWallet, marketplaceFee);
            erc20.transfer(_nftSeller, sellerFee);
        } else {
            payable(marketplaceWallet).transfer(marketplaceFee);
            payable(_nftSeller).transfer(sellerFee);
        }

        _transferNftToRecipient(_auctionId, _bidId);

        delete auctionToBid[_auctionId][_bidId];
        _cleanAuction(_auctionId);

        emit NFTTransferredAndSellerPaid(
            _auctionId, 
            _bidId,
            idToAuction[_auctionId].tokenIds,
            idToAuction[_auctionId].quantities,
            _nftBid,
            msg.sender,
            _getNftRecipient(_auctionId, _bidId),
            block.timestamp
        );
    }

    /// @notice Lazily mint unavailable NFTs and transfer them to buyer
    function _transferNftToRecipient(uint256 _auctionId, uint256 _bidId) 
    internal nonReentrant {
        ArtToken arttoken = ArtToken(idToAuction[_auctionId].nftContractAddress);
        address _nftRecipient = _getNftRecipient(_auctionId, _bidId);

        // cannot use ``batchMint()`` since not the marketplace does not have rights to mint all NFTs 
        uint256[] memory tokenIds = idToAuction[_auctionId].tokenIds;
        uint256[] memory quantities = idToAuction[_auctionId].quantities;
        for (uint256 i = 0; i < tokenIds.length; i++) {
            uint256 quantitiesToMint = quantities[i] - idToAuction[_auctionId].transferredQuantities[i];
            if (quantitiesToMint > 0) {
                arttoken.mint(
                    address(this), 
                    tokenIds[i], 
                    quantitiesToMint,
                    ""
                );
            }
        }

        arttoken.safeBatchTransferFrom(
            address(this), _nftRecipient, tokenIds, quantities, ""
        );
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║  TRANSFER NFT & PAY SELLER   ║
      ╚══════════════════════════════╝*/
    /**********************************/

    /*╔══════════════════════════════╗
      ║      SETTLE & WITHDRAW       ║
      ╚══════════════════════════════╝*/
    /// @notice For marketplace owner to stop an outdated auction
    function stopAuction(uint256 _auctionId) 
    external onlyOwner auctionOngoing(_auctionId) {
        idToAuction[_auctionId].isOnGoing = false;

        // If there is no bid made to the auction, the auction is withdrawn
        // otherwise it is left on the marketplace until the seller settles the auction
        if (idToAuction[_auctionId].numBids == 0) {
            _withdrawNftFromAuctionContract(_auctionId);
            _cleanAuction(_auctionId);
        }

        emit AuctionStopped(_auctionId);
    }

    /// @notice The seller can settle an auction after termination to take a bid and delete the auction
    function settleAuction(
        uint256 _auctionId, 
        uint256 _bidId, 
        bool takeHighestBid
    ) external onlyNftSeller(_auctionId) {
        require(
            !idToAuction[_auctionId].isOnGoing,
            "Marketplace.settleAuction# AUCTION_NOT_OVER"
        );
        if (takeHighestBid) {
            _bidId = idToAuction[_auctionId].highestBidId;
        }
        require(
            _isBidMade(_auctionId, _bidId), 
            "Marketplace.settleAuction# INVALID_BID"
        );
        _transferNftAndPaySeller(_auctionId, _bidId);
        emit AuctionSettled(_auctionId, _bidId);
    }

    /// @notice The seller can prematurely close an auction, or withdraw an ended auction without taking offer
    /// @dev Marketplace owner can use this function to withdraw an auction, which has been for too long without interaction
    /// @dev Withdraw NFTs from marketplace, withdraw all bids from the auction, then delete the auction
    function withdrawAuction(uint256 _auctionId) external {
        require(
            (msg.sender == idToAuction[_auctionId].nftSeller) ||
            (msg.sender == owner()),
            "Marketplace.onlyNftSeller# ONLY_NFT_SELLER"
        );
        _withdrawNftFromAuctionContract(_auctionId);
        _cleanAuction(_auctionId);
        emit AuctionWithdrawn(_auctionId);
    }

    /// @notice Withdraw bid and pay money back
    /// @dev Withdrawer must pay the gas to repick the highest bidder
    /// @dev If everybody has withdrawn their bid, ``numBids`` is set to zero
    /// @dev Withdrawn cash will be credited to the marketplace
    function withdrawBid(uint256 _auctionId, uint256 _bidId) 
    external auctionOngoing(_auctionId) {
        Bid memory bid = auctionToBid[_auctionId][_bidId];
        // _isBidMade(_auctionId, _bidId) is implicitly checked here
        require(
            msg.sender == bid.bidder, 
            "Marketplace.withdrawBid# ONLY_BIDDER_CAN_WITHDRAW_BID"
        );

        // repick highest bid
        uint256 highestBidId = 0;
        uint256 highestBid = 0;
        uint256 numBids = idToAuction[_auctionId].numBids; 
        for (uint256 i = 0; i < numBids; i++)
            if (
                (auctionToBid[_auctionId][i].bid > highestBid) &&
                (i != _bidId)
            ) {
                highestBidId = i;
                highestBid = auctionToBid[_auctionId][i].bid;
            }

        // check if all bidders has withdrawn their bids, if yes then update ``numBids`` to zero
        if (highestBid > 0) {
            idToAuction[_auctionId].highestBidId = highestBidId;
        } else {
            idToAuction[_auctionId].highestBidId = 0;
            idToAuction[_auctionId].numBids = 0;
        }

        // transfer withdrawn bid to the caller
        if (_isERC20Auction(_auctionId)) {
            tokenCredits[bid.bidder] += bid.bid;
        } else {
            ethCredits[bid.bidder] += bid.bid;
        }

        // delete bid
        delete auctionToBid[_auctionId][_bidId];

        emit BidWithdrawn(_auctionId, _bidId);
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║      SETTLE & WITHDRAW       ║
      ╚══════════════════════════════╝*/
    /**********************************/

    /*╔══════════════════════════════╗
      ║       UPDATE AUCTION         ║
      ╚══════════════════════════════╝*/
    /// @notice The seller can update whitelisted buyer for a fixed-price sale
    /// @dev Cannot update whitelisted buyer if a bid has been made
    function updateWhitelistedBuyer(
        uint256 _auctionId, 
        address _newWhitelistedBuyer
    ) external auctionOngoing(_auctionId) onlyNftSeller(_auctionId) {
        require(
            idToAuction[_auctionId].auctionType == 0, 
            "Marketplace.updateWhitelistedBuyer# NOT_A_SALE"
        );
        require(
            !_isABidMade(_auctionId), 
            "Marketplace.updateWhitelistedBuyer# A_BID_HAS_BEEN_MADE"
        );
        idToAuction[_auctionId].whitelistedBuyer = _newWhitelistedBuyer;
        emit WhitelistedBuyerUpdated(_auctionId, _newWhitelistedBuyer);
    }

    /// @notice The seller can update reserve price for timed auction
    function updateMinimumPrice(uint256 _auctionId, uint128 _newMinPrice)
        external
        auctionOngoing(_auctionId)
        onlyNftSeller(_auctionId)
        minPriceDoesNotExceedLimit(idToAuction[_auctionId].buyNowPrice, _newMinPrice)
    {
        require(
            idToAuction[_auctionId].auctionType == 1, 
            "Marketplace.updateMinimumPrice# NOT_AN_AUCTION"
        );
        idToAuction[_auctionId].minPrice = _newMinPrice;
        emit MinimumPriceUpdated(_auctionId, _newMinPrice);
    }

    /// @notice The seller can update buy-now price
    /// @dev Buy-now price cannot be updated for declining-price sale
    /// @dev If buy-now price is met by the highest bid, the sale is immediately terminated, and the highest bid is automatically taken
    function updateBuyNowPrice(uint256 _auctionId, uint128 _newBuyNowPrice)
        external
        auctionOngoing(_auctionId)
        onlyNftSeller(_auctionId)
        minPriceDoesNotExceedLimit(_newBuyNowPrice, idToAuction[_auctionId].minPrice)
    {
        require(
            idToAuction[_auctionId].auctionType != 2,
            "Marketplace.updateBuyNowPrice# IMMUTABLE_BUYNOW_PRICE"
        );
        idToAuction[_auctionId].buyNowPrice = _newBuyNowPrice;

        emit BuyNowPriceUpdated(_auctionId, _newBuyNowPrice);

        uint256 highestBidId = idToAuction[_auctionId].highestBidId;
        if (_isBuyNowPriceMet(_auctionId, highestBidId)) {
            _transferNftAndPaySeller(_auctionId, highestBidId);
        }
    }

    /// @notice The NFT seller can take offer and terminate an ongoing fixed-price sale
    function takeOffer(uint256 _auctionId, uint256 _bidId)
    external auctionOngoing(_auctionId) onlyNftSeller(_auctionId) {
        // _isBidMade(_auctionId, _bidId) is implicitly checked here
        require(
            idToAuction[_auctionId].auctionType == 0,
            "Marketplace.takeOffer# OFFERS_ARE_ALLOWED_ONLY_IN_FIXED_PRICE_SALE"
        );
        require(
            auctionToBid[_auctionId][_bidId].isOffer, 
            "Marketplace.takeOffer# NOT_AN_OFFER"
        );
        _transferNftAndPaySeller(_auctionId, _bidId);
        emit OfferTaken(_auctionId, _bidId);
    }

    /// @notice Withdraw credited value or ERC20 tokens from marketplace
    /// @dev Withdraw all credited value from marketplace and set the credit variable to zero
    function withdrawAllCredits() external nonReentrant {
        // transfer ETH credits
        uint256 ethAmount = ethCredits[msg.sender];
        if (ethAmount > 0) {
            ethCredits[msg.sender] = 0;
            payable(msg.sender).transfer(ethAmount);
        }

        // transfer token credits
        uint256 tokenAmount = tokenCredits[msg.sender];
        if (tokenAmount > 0) {
            tokenCredits[msg.sender] = 0;
            IERC20Upgradeable(erc20TokenForAuction).transfer(msg.sender, tokenAmount);
        }

        emit CreditWithdrawn(ethAmount, tokenAmount);
    }

    /**********************************/
    /*╔══════════════════════════════╗
      ║             END              ║
      ║       UPDATE AUCTION         ║
      ╚══════════════════════════════╝*/
    /**********************************/
}

